import React, {useEffect, useState} from "react";
import io from "socket.io-client";
import './AdminPanel.css'
const socket = io('http://localhost:4000'); // URL вашего сервера

function AdminPanel() {
    const [users, setUsers] = useState([]);
    const [messages, setMessages] = useState([]);
    useEffect(() => {
        // Получаем сообщение от сервера
        socket.on('server-message', (msg) => {
            setMessages((prevMessages) => [...prevMessages, msg]);
        });

        return () => {
            socket.off('server-message');
        };
    }, []);
    function formatDateTime(dateTimeString) {
        // Создаем объект даты из строки
        const date = new Date(dateTimeString);

        // Извлекаем необходимые компоненты даты и времени
        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0'); // Месяцы в JavaScript начинаются с 0
        const hours = String(date.getHours()).padStart(2, '0');
        const minutes = String(date.getMinutes()).padStart(2, '0');

        // Формируем строку в нужном формате
        return `${day}.${month} в ${hours}.${minutes}`;
    }
    return(
        <div className="AdminPanel">
            <div className="AdminPanel__header">Панель администратора</div>
            <h1>Запросы на выход</h1>
            <div>
                {messages.map((msg, index) => (
                    <div key={index}>{formatDateTime(msg.split(' ')[msg.split(' ')-1])}</div>
                ))}
            </div>
        </div>
    )
}
export default AdminPanel;
