import {useState} from "react";
import ShowModalWindow from "../components/ModalWindow";

function LoginPage({setUser, setRole}) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    function authenticate() {
        const data = {email: email, password: password};
        setUser(email);
        if ((email==='k.k.nayduk@school1298.ru') && (password==='xzSAwq21')) {
            setUser(email);
            setRole('admin')
            console.log(data);
        }else{
            setUser(email);
            setRole('client')
        }

    }
    return(
        <div className="LoginPage">
            <div className={"login-form"}>
                <label htmlFor="loginFormEmail">Email address</label>
                <input id={"loginFormEmail"} name={'email'} type={"email"} onChange={(e) => setEmail(e.target.value)}/>
                <label  htmlFor="loginFormPassword">Password</label>
                <input id={"loginFormPassword"} name={'password'} type={"password"} onChange={(e) => setPassword(e.target.value)}/>
                <button name={'login'} onClick={authenticate}>Войти</button>
            </div>
        </div>
    )
}
export default LoginPage;
