import React, {useEffect, useState} from "react";

function CardOut({msg}) {
    const [timer, setTimer] = useState(0);
    const [confirm, setConfirm] = useState(false);
    const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString());
    useEffect(() => {
        // Функция для обновления времени каждую секунду
        const intervalId = setInterval(() => {
            const now = new Date().toLocaleTimeString();
            setCurrentTime(now);

            if (Number(new Date()) > TARGET_TIME) {
                setCurrentTime(true);
                clearInterval(intervalId); // Останавливаем интервал после достижения времени
            }
        }, 1000);

        return () => clearInterval(intervalId); // Очищаем интервал при размонтировании компонента
    }, []);


    function formatMessage(message){
        const childName = message.split(' ')[0] +' '+ message.split(' ')[1];
        const timeOut = formatDateTime(message.split(' ')[message.split(' ').length-1])
        const dateOut = Number(new Date(message.split(' ')[message.split(' ').length-1]))
        const dateNow = Number(new Date())


        return {name:childName, timeOut:timeOut, confirmOut:dateOut <= dateNow?'Выход разрешен':'Выход запрещен'};
    }

    function formatDateTime(dateTimeString) {
        // Создаем объект даты из строки
        const date = new Date(dateTimeString);

        // Извлекаем необходимые компоненты даты и времени
        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0'); // Месяцы в JavaScript начинаются с 0
        const hours = String(date.getHours()).padStart(2, '0');
        const minutes = String(date.getMinutes()).padStart(2, '0');

        // Формируем строку в нужном формате
        return `${day}.${month} в ${hours}:${minutes}`;
    }
    return(
        <div className="CardOut" style={{backgroundColor:(formatMessage(msg).confirmOut==='Выход разрешен')?'green':'red'}}>

                <h4>Имя ученика: <span>{formatMessage(msg).name}</span></h4>
                <h4>Время выхода: <span>{formatMessage(msg).timeOut}</span></h4>
                <h4><span>{formatMessage(msg).confirmOut}</span></h4>

        </div>
    )
}

export default CardOut;
