import React, {useEffect, useState} from "react";

function CardOut({msg, order, filterText, filteredMessages}) {
    const [timer, setTimer] = useState(0);
    const [confirm, setConfirm] = useState(false);
    const [currentTime, setCurrentTime] = useState(Number(Date.now()));
    const [loading, setLoading] = useState();
    const dateOut = Number(new Date(msg.split(' ')[msg.split(' ').length-1]))
    const childName = msg.split(' ')[0] +' '+ msg.split(' ')[1];
    const [count, setCount] = useState(0);
    const [isRunning, setIsRunning] = useState(true);


    useEffect(() => {

        setLoading(true)
        if (currentTime > dateOut) {
            setConfirm(true)
        }
        else setConfirm(false)
        setLoading(false)

    }, [filterText,filteredMessages]);

    useEffect(() => {
        let interval;

        if (isRunning) {

            // Устанавливаем интервал для обновления компонента каждую секунду
            interval = setInterval(() => {
                setCount(prevCount => prevCount + 1);
                setLoading(true)
                if (Date.now() > dateOut) {
                    setConfirm(true)
                }
                else setConfirm(false)
                setLoading(false)
                console.log('Компонент обновлен')
            }, 1000);
        }

        // Очистка интервала при размонтировании или изменении isRunning
        return () => {
            if (interval) {
                clearInterval(interval);
            }
        };
    }, [isRunning]); // Используем isRunning как зависимость


    function formatMessage(message){
        const childName = message.split(' ')[0] +' '+ message.split(' ')[1];
        const timeOut = formatDateTime(message.split(' ')[message.split(' ').length-1])
        const dateOut = Number(new Date(message.split(' ')[message.split(' ').length-1]))
        const dateNow = Date.now()
        return {name:childName, timeOut:timeOut, confirmOut:dateOut <= dateNow?'Выход разрешен':'Выход запрещен'};
    }

    function formatDateTime(dateTimeString) {
        // Создаем объект даты из строки
        const date = new Date(dateTimeString);

        // Извлекаем необходимые компоненты даты и времени
        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0'); // Месяцы в JavaScript начинаются с 0
        const hours = String(date.getHours()).padStart(2, '0');
        const minutes = String(date.getMinutes()).padStart(2, '0');

        // Формируем строку в нужном формате
        return `${day}.${month} в ${hours}:${minutes}`;
    }
    return(
        !loading&&<div className="CardOut" style={{backgroundColor:confirm?'green':'red', order:-order}}>
                <h4>Имя ученика: <span style={{textTransform:'uppercase'}}>{childName}</span></h4>
                <h4>Время выхода: <span>{formatMessage(msg).timeOut}</span></h4>
                <span style={{textTransform:'uppercase', marginTop:'11px', fontWeight:'bold'}}>{formatMessage(msg).confirmOut}</span>
        </div>
    )
}

export default CardOut;
