import './ModalWindow.css'
import {useEffect, useState} from "react";
function ShowModalWindow({children}) {
    const [dateTime, setDateTime] = useState()
    const dateTimeNow = new Date()
    function getFormattedDateTime() {
        // Получаем текущую дату и время
        const now = new Date();

        // Преобразуем дату в строку ISO формата
        const isoString = now.toISOString();

        // Пример строки ISO: "2017-06-01T08:30:00.000Z"
        // Убираем секунды и миллисекунды
        const formattedDateTime = isoString.slice(0, 16);

        // Возвращаем результат
        return formattedDateTime;
    }

    return(
        <div className="modal-window">
            <div className="modal-window-body">
                <div className="modal-window-header">Пропуск на выход</div>
                <div className="modal-window-body">
                    <div className={"name"}>Авдеева</div>
                    <input type={'datetime-local'} value={getFormattedDateTime()} onChange={(e)=>setDateTime(e.target.value)}/>
                    <button onClick={()=>alert(dateTime)}></button>
                </div>
            </div>
        </div>
    )
}
export default ShowModalWindow;
