import React, {createContext, useContext, useEffect, useState} from "react";
import './LoginPage.css'

import logo1298 from '../assets/logo1298.svg'
import {useNavigate} from "react-router-dom";
import axios from "axios";
import {UserId} from "../App";
function LoginForm() {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [error, setError] = React.useState('');
    const navigate = useNavigate();
    const user = useContext(UserId);

    async function loginUser(email, password) {
        try {
            const response = await axios.post('http://91.77.160.177:4001/login', {
                email: email,
                password: password
            });

            console.log('Успешный вход:', response.data);

            navigate('/client')
            // Сохраните токен доступа или выполните другие действия
            localStorage.setItem('access_token', response.data.access_token);
            return response.data; // Возвращаем ответ для дальнейшей обработки
        } catch (error) {
            if (error.response) {
                // Сервер вернул ответ с кодом состояния, который не в диапазоне 2xx
                console.error('Ошибка входа:', error.response.data.msg);
            } else if (error.request) {
                // Запрос был сделан, но ответа не получено
                console.error('Ошибка: ответ от сервера не получен', error.request);
            } else {
                // Произошла ошибка при настройке запроса
                console.error('Ошибка:', error.message);
            }
            throw error; // Можно выбросить ошибку дальше для обработки
        }
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await loginUser(email, password);
            // Возможно, перенаправление или другое действие после успешного входа
        } catch (error) {
            // Обработка ошибок, например, отображение сообщения пользователю
            alert('Ошибка входа. Проверьте свои учетные данные.');
        }
    };
    useEffect(()=>{

    }, [error,setError])
    return (
        <div className="login-container" style={{display:"flex", flexDirection:"column", alignItems:"center"}}>
            <img src={logo1298} className="logo" alt="logo" style={{width:'200px'}}/>
            <h2>Классный помошник <br/>Школа №1298 </h2>
            <form onSubmit={handleSubmit}>
                <div className="input-group">
                    <label htmlFor="email">E-mail:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        style={{width:'300px'}}
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="password">Пароль:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        style={{width:'300px'}}
                    />
                </div>
                <div className="button-group">
                    <button type="submit" style={{width:'160px'}}>Войти</button>
                </div>
                {error.length>0&&<div style={{marginTop:'8px',textAlign:'center', color:'red'}}>{error}</div>}
            </form>
        </div>
    );
}
export default LoginForm;
