import React from "react";

function CardOut({ msg, order }) {
    function formatMessage(message) {
        const parts = message.split(' ');
        const childName = parts[0] + ' ' + parts[1];
        const timeOut = formatDateTime(parts[parts.length - 1]);
        const dateOut = new Date(parts[parts.length - 1]).getTime();
        const dateNow = Date.now();
        const confirmOut = dateOut <= dateNow ? 'Выход разрешен' : 'Выход запрещен';

        return { name: childName, timeOut, confirmOut };
    }

    function formatDateTime(dateTimeString) {
        // Создаем объект даты из строки
        const date = new Date(dateTimeString);

        // Используем toLocaleString для корректного форматирования
        const options = {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric',
            hour: '2-digit',
            minute: '2-digit',
            hour12: false, // 24-часовой формат
        };

        return date.toLocaleString('ru-RU', options).replace(',', ' в');
    }

    const { name, timeOut, confirmOut } = formatMessage(msg);

    return (
        <div className="CardOut" style={{ backgroundColor: confirmOut === "Выход разрешен" ? 'green' : 'red', order: -order }}>
            <h4>Имя ученика: <span style={{ textTransform: 'uppercase' }}>{name}</span></h4>
            <h4>Время выхода: <span>{timeOut}</span></h4>
            <span style={{ textTransform: 'uppercase', marginTop: '11px', fontWeight: 'bold' }}>{confirmOut}</span>
        </div>
    );
}

export default CardOut;
