import React, {useContext, useEffect, useState} from 'react';
import CardOut from "./CardOut";
import ChildCard from "./ChildCard";
import axios from "axios";
import {UserId} from "../App";

const History = () => {
    const { user } = useContext(UserId);
    const [history, setHistory] = useState([]);
    function getHistoryOuts(){
        axios.get(`http://91.77.160.177:4001/get_history_user/${user.id}`).then((response) => {
            console.log(response);
            setHistory(response.data.history_list);
        })
    }

    useEffect(() => {
        getHistoryOuts()
    }, []);
    return (
        <div className={'card-out-list'} style={{width:'100%', display:'flex', flexDirection:'column', alignItems:'center'}}>
            <h1 style={{margin:0, fontSize:'25px', marginTop:'10px', fontWeight:'400'}}>История выходов</h1>
            {history.map(el=><ChildCard childCard={{fullName:el.student_name}}/>)}
        </div>
    );
};

export default History;
