import React, {useEffect, useState} from "react";
import './LoginPage.css'

import logo1298 from '../assets/logo1298.svg'
import {useNavigate} from "react-router-dom";
function LoginForm() {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [error, setError] = React.useState('');
    const navigate = useNavigate();

    
    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await loginUser(username, password);
            // Возможно, перенаправление или другое действие после успешного входа
        } catch (error) {
            // Обработка ошибок, например, отображение сообщения пользователю
            alert('Ошибка входа. Проверьте свои учетные данные.');
        }
    };
    useEffect(()=>{

    }, [error,setError])
    return (
        <div className="login-container" style={{display:"flex", flexDirection:"column", alignItems:"center"}}>
            <img src={logo1298} className="logo" alt="logo" style={{width:'200px'}}/>
            <h2>Классный помошник <br/>Школа №1298 </h2>
            <form onSubmit={handleSubmit}>
                <div className="input-group">
                    <label htmlFor="email">E-mail:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        style={{width:'300px'}}
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="password">Пароль:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        style={{width:'300px'}}
                    />
                </div>
                <div className="button-group">
                    <button type="submit" style={{width:'160px'}}>Войти</button>
                </div>
                {error.length>0&&<div style={{marginTop:'8px',textAlign:'center', color:'red'}}>{error}</div>}
            </form>
        </div>
    );
}
export default LoginForm;
