import MultiSelectDropdown from "../components/MultiSelectDropdown";
import React, {useContext} from "react";
import {useOutlet, useOutletContext} from "react-router-dom";
import '../components/ChildCard.css'
function AbcientPage() {
    const [dataStudentContext, setDataStudentsContext] = useOutletContext()
    return (
        <div className="AbcientPage">

            <h1 style={{textAlign: 'center', marginTop: '10px', color: 'white', fontSize: '25px'}}>Отсутствующие</h1>
            <MultiSelectDropdown data={dataStudentContext} name={'по болезни'}/>
            <MultiSelectDropdown data={dataStudentContext} name={'по семейным обстоятельствам'}/>
            <MultiSelectDropdown data={dataStudentContext} name={'без уважительной причины'}/>
            <MultiSelectDropdown data={dataStudentContext} name={' - заочное обучение'}/>
        </div>

    )
}

export default AbcientPage;
