import React, {useEffect, useState} from "react";
import './App.css';
import Header from "./components/Header";
import MainPage from "./pages/MainPage";

import io from 'socket.io-client';
import AdminPanel from "./pages/AdminPanel";
const socket = io('http://localhost:4000'); // URL вашего сервера

function App() {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        // Получаем сообщение от сервера
        socket.on('server-message', (msg) => {
            setMessages((prevMessages) => [...prevMessages, msg]);
        });

        return () => {
            socket.off('server-message');
        };
    }, []);
    const [user,setUser] = useState('');

    const sendMessage = () => {
        // Отправляем сообщение на сервер
        socket.emit('client-message', message);
        setMessage('');
    };

    const [className, setClassName] = React.useState('11А');
  return (
      <div className="App">
          {!(user==='admin'||user==='client')&&<div>
              <input type={'text'} value={user} onChange={(e) => setUser(e.target.value)}/>
          </div>}
          {(user==='admin')?
              <AdminPanel messages={messages}/>
          :(user==='client')?<div><Header className={className}/>
              <MainPage sendMessage={sendMessage} setMessage={setMessage}/></div>:<div></div>}
      </div>
  );
}

export default App;
