import React, { useEffect, useState } from "react";

function CardOut({ msg, order }) {
    const [ready, setReady] = useState(false);  // состояние загрузки
    const [currentTime, setCurrentTime] = useState(new Date());  // текущее время
    const [messageData, setMessageData] = useState({});

    useEffect(() => {
        const parseMessage = (message) => {
            const parts = message.split(", ");
            const namePart = parts.length > 0 ? parts[0] : "";
            const timePart = parts.length > 1 ? parts[1] : "";
            return { name: namePart, time: timePart };
        };

        // Парсинг сообщения
        const parsedData = parseMessage(msg);
        setMessageData(parsedData);
        setReady(true);  // компонент готов

        // Обновляем текущее время каждую секунду
        const intervalId = setInterval(() => {
            setCurrentTime(new Date());
        }, 1000);

        // Очищаем интервалы при размонтировании компонента
        return () => clearInterval(intervalId);
    }, [msg]);

    // Функция для форматирования времени
    const formatDateTime = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    };

    // Проверка времени выхода
    const handleTimeCheck = () => {
        const messageTime = new Date(messageData.time);
        return currentTime >= messageTime ? 'Выход разрешен' : 'Выход не разрешен';
    };

    // Отображаем контент компонента только после его загрузки
    if (!ready) {
        return <div>Загрузка...</div>;
    }

    return (

        <div className="CardOut"
             style={{ backgroundColor: messageData.confirmOut === "Выход разрешен" ? 'green' : 'red', order: -order }}>

            <h4>Имя ученика: <span style={{ textTransform: 'uppercase' }}>{messageData.name}</span></h4>
            <h4>Время выхода: <span>{messageData.timeOut}</span></h4>
            <span style={{
                textTransform: 'uppercase',
                marginTop: '11px',
                fontWeight: 'bold'
            }}>{messageData.confirmOut}</span>
        </div>
    );
}

export default CardOut;
