import './PagesStyle.css'

import ListChildCard from "../components/ListChildCard";
import ModalWindow from "../components/ModalWindow";
import React, {useState} from "react";
import Header from "../components/Header";
function ClientPanel() {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [modal, setModal] = React.useState(false);
    const [activeChildCardName, setActiveChildCard] = React.useState({fullName:'', id:''});
    const sendMessage = () => {
        // Отправляем сообщение на сервер
        socket.emit('client-message', message);
        setMessage('');
    };
    return(
        <div className="main-page">
            <Header classGrad={'11А'}/>
            <h1>Мой класс</h1>
            <ListChildCard setModal={setModal} setActiveChildCard={setActiveChildCard} activeChildCardName={activeChildCardName} />
            <ModalWindow sendMessage={sendMessage} setMessage={setMessage} isOpen={modal} setModal={setModal} setActiveChildCard={setActiveChildCard} fullName={activeChildCardName.fullName} id={activeChildCardName.id}  />
        </div>
    )
}
export default ClientPanel;
