import React, {useState} from "react";
import ChildCard from "./ChildCard";

function ListChildCard({setModal, isOpen, setActiveChildCard, activeChildCardName}) {
    const listChildCard = [{id: 0, fullName:"Авдеева Юлия", numberPhone: '89850638581', photoSrc:''},
        {id: 0, fullName:"Авдеева Юлия", numberPhone: '89850638581', photoSrc:''},
        {id: 1, fullName:"Аракчеев Артем", numberPhone: '89850638581', photoSrc:''},
        {id: 2, fullName:"Найдюк Антон", numberPhone: '89850638581', photoSrc:''},
        {id: 3, fullName:"Михайлов Глеб", numberPhone: '89850638581', photoSrc:''},]
    const [childCards, setChildCards] = useState(listChildCard);

    return(
        <div className="ListChildCard" style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
            <h1 style={{textAlign: 'center', marginTop:'10px', color:'white', fontSize:'25px'}}>Мой класс</h1>
            {childCards.map((childCard, index) => (
                <ChildCard key={index} childCard={childCard} activeChildCardName={activeChildCardName}
                           setModal={setModal} isOpen={isOpen} setActiveChildCard={setActiveChildCard}/>))}
        </div>
    )
}

export default ListChildCard;
