import './Abcient.css'
import React from 'react'
import moment from "moment";
const AbcientModal = ({id, fullName}) =>{
    const [isOpen, setIsOpen] = React.useState(true);
    const [cause, setCause] = React.useState('inSchool');

    function handleSend(){
        setIsOpen(false);
        const date = moment().format();
        console.log(id, fullName)
        console.log(cause);
        console.log(date);
    }


    return(
        <div className={isOpen?"abcent-modal":"abcent-modal close"}>

            <div className="abcent-modal__body"
                 style={{display: 'grid', gridTemplateColumns: '1fr 1 fr 1fr', justifyContent: 'space-between', padding:'10px', background: 'white', borderRadius:'5px'}}>
                <div className="">{fullName}</div>
                <select value={cause} onChange={(e) => setCause(e.target.value)}>
                    <option value={'inSchool'}>Присутствует</option>
                    <option value={'by'}>БУ</option>

                </select>
                <button onClick={() => handleSend()}>Отправить</button>
            </div>
        </div>
    )
}
export default AbcientModal;
