import React, {useEffect, useState} from "react";
import './LoginPage.css'
import logo from '../assets/logo.svg'
import {useNavigate} from "react-router-dom";
function LoginForm() {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [error, setError] = React.useState('');
    const navigate = useNavigate();
    const handleSubmit = (event) => {
        event.preventDefault();
        if (email.length >0 && password.length >0) {
            if ((email==='admin@gmail.com') && (password==='admin')){
                navigate('/admin')
            }else{
                if ((email==='client@gmail.com') && (password==='client')){
                    navigate('/client')
                }
                else {
                    setError('Неверные данные')
                }
            }


        }
        console.log('Входящие данные:', { email, password });
    };
    useEffect(()=>{

    }, [error,setError])
    return (
        <div className="login-container" style={{display:"flex", flexDirection:"column", alignItems:"center"}}>
            <img src={logo} className="logo" alt="logo" style={{width:'69px'}}/>
            <h2>Вход</h2>
            <form onSubmit={handleSubmit}>
                <div className="input-group">
                    <label htmlFor="email">E-mail:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        style={{width:'300px'}}
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="password">Пароль:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        style={{width:'300px'}}
                    />
                </div>
                <div className="button-group">
                    <button type="submit" style={{width:'50px'}}>Войти</button>
                </div>
                {error.length>0&&<div style={{marginTop:'8px',textAlign:'center', color:'red'}}>{error}</div>}
            </form>
        </div>
    );
}
export default LoginForm;
