import React, {useState} from "react";
import './Footer.css'
import home from '../assets/homeicon.svg'
import exit from '../assets/exitacc.svg'
import attention from '../assets/attention.svg'
import {useNavigate} from "react-router-dom";
function Footer() {
    const navigate = useNavigate();
    function handleSubmit() {
        localStorage.removeItem('user');
        navigate('/')
    }
    const [isStyledAdd, setIsStyledAdd] = useState(false);
    const [isStyledRemove, setIsStyledRemove] = useState(false);

    // Обработчик для добавления стиля к кнопке
    const addStyle = () => {
        setIsStyledAdd(true);
        setIsStyledRemove(false); // Убираем стиль с другой кнопки
    };

    // Обработчик для удаления стиля с кнопки
    const removeStyle = () => {
        setIsStyledRemove(true);
        setIsStyledAdd(false); // Убираем стиль с другой кнопки
    };
    function handleSubmit2(path) {
        if (path===''){
            addStyle()
            navigate(path)
        }else if (path==='abcient'){
            removeStyle()
        }
        navigate(path)
    }
    return (
        <div className={'nav-bar'} style={{display: 'flex', justifyContent: 'space-around'}}>
            <button onClick={()=>handleSubmit2('')} style={{borderBottom:isStyledAdd&&'solid white', height:'min-content' }} ><img className={'icons-menu'} src={home} alt={'home'}/></button>
            <button onClick={()=>handleSubmit2('abcient')} style={{borderBottom:isStyledRemove&&'solid white', height:'min-content' }}><img className={'icons-menu'} src={attention} alt={'home'}/></button>
            <button onClick={() => handleSubmit()}><img className={'icons-menu'} src={exit} alt={'home'}/></button>
        </div>
    )

}

export default Footer;
