import MultiSelectDropdown from "../components/MultiSelectDropdown";
import React, {useContext} from "react";
import {useOutlet, useOutletContext} from "react-router-dom";
import '../components/ChildCard.css'
import AbcientModal from "../components/AbcientModal";
import moment from "moment";
function AbcientPage() {
    moment.locale('ru-RU')
    const [className, dataStudents, isLoading] = useOutletContext()

    return (
        !isLoading&&<div className="AbcientPage" style={{alignItems:"center", justifyContent:"center", width:'100%', display:"flex", flexDirection:'column'}} >
            <h1>{moment().format('LL')}</h1>
            <h1 style={{textAlign: 'center', marginTop: '10px', color: 'white', fontSize: '25px'}}>Отсутствующие</h1>
            <div style={{
                width: '200px',
                backgroundColor: 'white',
                height: '2px',
                marginBottom: '10px',
                marginTop: '10px'
            }}></div>
        <div style={{display:'flex', flexDirection:'column', gap:'10px'}}>
            {dataStudents.map((student, index) => (<AbcientModal fullName={student.fullName} id={student.id} key={student.id}/>))}
        </div>

        </div>

    )
}

export default AbcientPage;
