import React, { useEffect, useState } from "react";

function CardOut({ msg, order }) {
    const [messageData, setMessageData] = useState({});
    const [dateO, setDateO] = useState('');

    useEffect(() => {
        const formattedData = formatMessage(msg);
        setMessageData(formattedData);
        setDateO(formattedData.timeOut);

        // Устанавливаем интервал для обновления состояния каждую секунду
        const interval = setInterval(() => {
            const now = Date.now();
            const confirmOut = formattedData.dateOut <= now + 3 * 60 * 60 * 1000 ? 'Выход разрешен' : 'Выход запрещен';
            console.log(dateO)
            setMessageData(prevData => ({
                ...prevData,
                confirmOut: confirmOut,
            }));
        }, 1000);

        // Очистка интервала при размонтировании компонента
        return () => clearInterval(interval);
    }, [msg]);  // Зависимость от msg, чтобы при смене сообщения обновить форматирование

    function parseDate(dateString) {
        // Удаляем 'Z' в конце строки, если он присутствует
        if (dateString.endsWith('Z')) {
            dateString = dateString.slice(0, -1);
        }

        const [datePart, timePart] = dateString.split('T');
        const [year, month, day] = datePart.split('-').map(Number);
        const [hours, minutes] = timePart.split(':').map(Number);

        // Возвращаем дату в UTC
        return new Date(Date.UTC(year, month - 1, day, hours, minutes));
    }

    function formatMessage(message) {
        const parts = message.split(' ');
        const childName = parts[0] + ' ' + parts[1];
        const timeString = parts[parts.length - 1];
        const parsedDate = parseDate(timeString); // Парсим дату

        const dateOut = parsedDate.getTime();

        // Форматируем сообщение на основе сравнения времени
        return {
            name: childName,
            timeOut: formatDateTime(parsedDate), // Используем уже распарсенную дату
            dateOut, // Сохраняем dateOut для дальнейшего использования
            confirmOut: dateOut <= Date.now() + 3 * 60 * 60 * 1000 ? 'Выход разрешен' : 'Выход запрещен',
        };
    }

    function formatDateTime(date) {
        const day = String(date.getUTCDate()).padStart(2, '0');
        const month = String(date.getUTCMonth() + 1).padStart(2, '0');
        const hours = String(date.getUTCHours()).padStart(2, '0');
        const minutes = String(date.getUTCMinutes()).padStart(2, '0');

        return `${day}.${month} в ${hours}:${minutes}`;
    }

    return (
        <div className="CardOut"
             style={{ backgroundColor: messageData.confirmOut === "Выход разрешен" ? 'green' : 'red', order: -order }}>
            <span>{dateO}</span>
            <h4>Имя ученика: <span style={{ textTransform: 'uppercase' }}>{messageData.name}</span></h4>
            <h4>Время выхода: <span>{messageData.timeOut}</span></h4>
            <span style={{
                textTransform: 'uppercase',
                marginTop: '11px',
                fontWeight: 'bold'
            }}>{messageData.confirmOut}</span>
        </div>
    );
}

export default CardOut;
