import React, {useEffect, useState} from "react";
import './App.css';
import Header from "./components/Header";
import ClientPanel from "./pages/ClientPanel";

import io from 'socket.io-client';
import AdminPanel from "./pages/AdminPanel";
import LoginPage from "./pages/LoginPage";
const socket = io('http://localhost:4000'); // URL вашего сервера

function App() {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);

    const [user,setUser] = useState(null);
    const [role, setRole] = useState('')
    const [className, setClassName] = React.useState('11А');

    function enterInApp(){
        if (!user===null){
            if (role==='admin'){
                return(<AdminPanel messages={messages}/>)
            }
            if (role==='client'){
                return(<ClientPanel sendMessage={sendMessage} setMessage={setMessage}/>)
            }
        }else {
            return <LoginPage user={user} setUser={setUser} role={role} setRole={setRole}/>
        }
    }
    useEffect(() => {
        // Получаем сообщение от сервера
        socket.on('server-message', (msg) => {
            setMessages((prevMessages) => [...prevMessages, msg]);
        });

        return () => {
            socket.off('server-message');
        };
    }, []);


    const sendMessage = () => {
        // Отправляем сообщение на сервер
        socket.emit('client-message', message);
        setMessage('');
    };


  return (
      <div className="App">
          {/*{!(user==='admin'||user==='client')&&<div>*/}
          {/*    <input type={'text'} value={user} onChange={(e) => setUser(e.target.value)}/>*/}
          {/*</div>}*/}
          {/*{(user==='admin')?*/}
          {/*    <AdminPanel messages={messages}/>*/}
          {/*:(user==='client')?<div><Header className={className}/>*/}
          {/*    <ClientPanel sendMessage={sendMessage} setMessage={setMessage}/></div>:<div></div>}*/}
          {enterInApp()}
      </div>
  );
}

export default App;
