import './Abcient.css'
import React, {useEffect} from 'react'
import moment from "moment";
const AbcientModal = ({id, fullName}) =>{
    const [cause, setCause] = React.useState('inSchool');
    const [color,setColor] = React.useState('green');

    function handleSend(){
        const date = moment().format();
        console.log(id, fullName)
        console.log(cause);
        console.log(date);
    }
    function changeCause(e){
        if(e.target.value === "inSchool"){
            setColor('green')
        }
        else{
            setColor('red')
        }
        setCause(e.target.value)

    }

    useEffect(() => {
        handleSend()
    }, [cause]);

    return(
                <select value={cause} onChange={(e) => changeCause(e)} style={{background:color, width:'20px', borderRadius:'5px'}}>
                    <option value={'inSchool'} style={{background:"white"}}>Присутствует</option>
                    <option value={'by'} style={{background:"white"}}>БУ</option>
                </select>
    )
}
export default AbcientModal;
