import ClientPanel from "../pages/ClientPanel";
import AbcientPage from "../pages/AbcientPage";
import Footer from "./Footer";
import {Outlet} from "react-router-dom";
import Header from "./Header";
import axios from "axios";
import React, {useContext, useEffect, useState} from "react";
import ProtectedRoute from "./ProtectedRoute";
import {UserId} from "../App";

function Root(){
    const { user } = useContext(UserId)
    const [dataStudents1, setStudents] = useState([])
    const [className, setClassName] = React.useState('');
    const [isLoading, setIsLoading] = useState(true);

    async function fetchClassInfoByTeacher(teacherId) {
        setIsLoading(true)
        try {
            const response = await axios.get(`http://91.77.160.177:4001/get_info_mentor/${teacherId}`);
            const { className, students } = response.data;
            return {className, students};
        } catch (error) {
            // Обрабатываем ошибки
            if (error.response) {
                // Сервер вернул ответ с кодом состояния, который выходит за пределы 2xx
                console.error('Ошибка при получении данных:', error.response.data);
            } else if (error.request) {
                // Запрос был сделан, но ответ не получен
                console.error('Нет ответа от сервера:', error.request);
            } else {
                // Произошла ошибка при настройке запроса
                console.error('Ошибка при настройке запроса:', error.message);
            }
        }
        setIsLoading(false)
    }

    useEffect(() => {
        setIsLoading(true)
        fetchClassInfoByTeacher(user.id).then(data=>{
            setClassName(data.className)
            setStudents(data.students)
            setIsLoading(false)
        })
        setIsLoading(false)
    }, []);
    return(
        <div className="Main">
            <Header nameApp={'Классный помощник'}/>
                <ProtectedRoute children={!isLoading&&<Outlet context={[className, dataStudents1, isLoading]} />}/>
            <Footer/>
        </div>
    )
}
export default Root;
