import ClientPanel from "../pages/ClientPanel";
import AbcientPage from "../pages/AbcientPage";
import Footer from "./Footer";
import {Outlet} from "react-router-dom";
import Header from "./Header";
import axios from "axios";
import {useContext} from "react";

function Main(){
    const teacherId = useContext(UserId);
    const [className, setClassName] = React.useState("");
    try {
        const response = axios.get(`http://91.77.160.177:4001/get_info_mentor/${teacherId}`);

        // Обрабатываем полученные данные
        const { className, students } = response.data;
        console.log(students);
        setClassName(className)
    }catch {
        console.log('Ошибка');
    }

    return(
        <div className="Main">
            <Header classGrad={className}/>
            <Outlet/>
            <Footer/>
        </div>
    )
}
export default Main;
