import './PagesStyle.css'

import ListChildCard from "../components/ListChildCard";
import ModalWindow from "../components/ModalWindow";
import React from "react";
function MainPage() {
    const [modal, setModal] = React.useState(false);
    const [activeChildCardName, setActiveChildCard] = React.useState('');
    return(
        <div className="main-page">
            <h1>Мой класс</h1>
            <ListChildCard setModal={setModal} setActiveChildCard={setActiveChildCard} />
            <ModalWindow isOpen={modal} setModal={setModal} fullName={activeChildCardName}  />
        </div>
    )
}
export default MainPage;
