import React, {useState} from "react";
import e from "cors";
import axios from "axios";
import {redirect, useNavigate} from "react-router-dom";
import logo1298 from "../assets/logo1298.svg";
function Registration() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [passwordConfirm, setPasswordConfirm] = useState("");
    const navigate = useNavigate();
    async function registerUser(email, password) {
        try {
            const response = await axios.post('http://91.77.160.177:4001/register', {
                email: email,
                password: password,
            });
            navigate('/')
            console.log('Успешная регистрация:', response.data);

            return response.data; // Возвращаем ответ для дальнейшей обработки
        } catch (error) {
            if (error.response) {
                // Запрос был сделан, и сервер ответил с кодом состояния, который
                // выходит за пределы диапазона 2xx
                console.error('Ошибка регистрации:', error.response.data.message);
            } else if (error.request) {
                // Запрос был сделан, но ответа не получено
                console.error('Ошибка: ответ от сервера не получен', error.request);
            } else {
                // Произошла ошибка при настройке запроса
                console.error('Ошибка:', error.message);
            }
            throw error; // Можно выбросить ошибку дальше для обработки в родительском компоненте
        }
    }
    const handleRegister = async () => {
        e.preventDefault();
        if ('@school1298.ru' in email){

        try {
            const result = await registerUser(email, password);
            console.log(result); // Успешная регистрация
        } catch (error) {
            console.error('Не удалось зарегистрироваться', error);
        }}else{
            alert('Email должен содержать только корпоративную электронную почту')
        }
    };
    return (
        <div className="login-container" style={{display: "flex", flexDirection: "column", alignItems: "center"}}>

            <img src={logo1298} className="logo" alt="logo" style={{width: '200px'}}/>
            <h2>Классный помошник <br/>Школа №1298 </h2>
            <h3>Регистрация</h3>
            <form onSubmit={handleRegister}>
                <div className="input-group">
                    <label htmlFor="email">E-mail:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        style={{width: '300px'}}
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="password">Пароль:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        style={{width: '300px'}}
                    />
                </div>
                <div className="button-group">
                    <button type="submit" style={{width: '160px', marginBottom: '20px'}}>Зарегистрироваться</button>
                </div>
                <span onClick={() => navigate('/')} style={{
                    textAlign: 'center',
                    color: "white",
                    marginTop: '20px',
                    textDecoration: 'underline'
                }}>Войти</span>

            </form>
        </div>
    )
}

export default Registration;
