import './Abcient.css'
import React from 'react'
import moment from "moment";
const AbcientModal = () =>{
    const [isOpen, setIsOpen] = React.useState(true);
    const [cause, setCause] = React.useState('Болен');

    function handleSend(){
        setIsOpen(false);
        const date = moment().format();
        console.log(cause);
        console.log(date);
    }


    return(
        <div className={isOpen?"abcent-modal":"abcent-modal close"}>
            <div className="abcent-modal__header">ФИО</div>
            <div className="abcent-modal__body">
                <div>Причина отсутствия:</div>
                <select value={cause} onChange={(e) => setCause(e.target.value)}>
                    <option value={'ill'}>Болен</option>
                    <option value={'by'}>БУ</option>

                </select>
                <button onClick={()=>handleSend()}>Отправить</button>
            </div>
        </div>
    )
}
export  default AbcientModal;
