import React, {useCallback, useEffect, useState} from "react";
import './App.css';
import Header from "./components/Header";
import ClientPanel from "./pages/ClientPanel";

import io from 'socket.io-client';
import AdminPanel from "./pages/AdminPanel";
import LoginPage from "./pages/LoginPage";


function App() {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const authToken = '123123';
    const [user,setUser] = useState('');
    const [role, setRole] = useState('')
    const [socket, setSocket] = useState(null);
    // eslint-disable-next-line react-hooks/exhaustive-deps

    const enterInApp = useCallback(() => {
        if (user !== '') {
            if (role === 'admin') {
                return (<AdminPanel messages={messages} />);
            } else if (role === 'client') {
                return (<ClientPanel sendMessage={sendMessage} setMessage={setMessage} />);
            }
        } else {
            return <LoginPage user={user} setUser={setUser} role={role} setRole={setRole} />;
        }
    }, [user, role, messages]);

    useEffect(() => {
        const socketConnection = io('http://localhost:4000', {
            query: { token: authToken }
        });

        setSocket(socketConnection);

        // Получение сообщений от сервера
        socketConnection.on('server-message', (msg) => {
            setMessages(prevMessages => [...prevMessages, msg]);
        });

        return () => {
            socketConnection.off('server-message');
            socketConnection.disconnect();
        };
    }, [authToken]);

    useEffect(() => {
        // Обработка входа в приложение на основе состояния пользователя и роли
        enterInApp();
    }, [user, role, enterInApp]);

    const sendMessage = () => {
        if (socket) {
            socket.emit('client-message', message);
            setMessage('');
        }
    };


  return (
      <div className="App">
          {/*{!(user==='admin'||user==='client')&&<div>*/}
          {/*    <input type={'text'} value={user} onChange={(e) => setUser(e.target.value)}/>*/}
          {/*</div>}*/}
          {/*{(user==='admin')?*/}
          {/*    <AdminPanel messages={messages}/>*/}
          {/*:(user==='client')?<div><Header className={className}/>*/}
          {/*    <ClientPanel sendMessage={sendMessage} setMessage={setMessage}/></div>:<div></div>}*/}
          {enterInApp()}
      </div>
  );
}

export default App;
