// src/pages/LoginForm.js
import React, { useContext, useState } from "react";
import { login } from "./services/authService"; // Импортируем нашу службу
import { UserId } from "../App";

function LoginForm() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const { setAuth } = useContext(UserId);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const data = await login(email, password);
            setAuth({ id: data.id, token: data.access_token });
            // Перенаправление на страницу клиента или администратора по роли
            // navigate('/client'); // или navigate('/admin');
        } catch (err) {
            setError('Ошибка входа: неверный логин или пароль');
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Пароль" required />
                <button type="submit">Войти</button>
                {error && <p>{error}</p>}
            </form>
        </div>
    );
}

export default LoginForm;
