import React, {useEffect, useState} from "react";
import io from "socket.io-client";
import './AdminPanel.css'
import CardOut from "../components/CardOut";
import logo from '../assets/logo.svg';
import Header from "../components/Header";
import axios from "axios";
const socket = io('http://91.77.160.177:4000/'); // URL вашего сервера

function AdminPanel() {
    const [messages, setMessages] = useState([]);
    const [filterText, setFilterText] = useState('');
    const handleFilterChange = (e) => {
        setFilterText(e.target.value);
    };
    const filteredMessages = messages.filter(msg =>
        msg.toLowerCase().includes(filterText.toLowerCase())
    );
    async function loadMessages() {
        await axios.get('http://91.77.160.177:4001/get_outs')
            .then(res => {
                console.log(res.data);
                res.data.map(msg => { setMessages((prevMessages) => [...prevMessages, msg]);});
            })
            .catch(err => console.log(err))
    }
    useEffect(() => {
        loadMessages()
        // Получаем сообщение от сервера
        socket.on('server-message', (msg) => {
            setMessages((prevMessages) => [...prevMessages, msg]);
        });

        return () => {
            socket.off('server-message');
        };
    }, []);


    return(
        <div className="AdminPanel">

            <Header classGrad={''} nameApp={'School1298 Admin Panel'}/>
            <h1>Запросы на выход</h1>
            <div className="filter-form" style={{display: 'flex', flexDirection: 'column',alignItems:"center", width:'100%'}}>
                <input
                    type="text"
                    placeholder="Поиск пропуска"
                    value={filterText}
                    onChange={handleFilterChange}
                    className="filter-input"
                    style={{height:'30px',width:'300px', fontSize:'15px', marginBottom:'10px',padding:'3px', textAlign:'center'}}

                />
            </div>
            <div className={'board'}>
                {filteredMessages.map((msg, index) => (
                    <CardOut index={index} key={index} msg={msg} order={index}/>
                ))}
            </div>
        </div>
    )
}

export default AdminPanel;
